import { createContext, useState, useEffect, useContext } from 'react';
import { toast } from 'react-hot-toast';
import api from '../api/axios';
import { v4 as uuidv4 } from 'uuid';

const AuthContext = createContext();

export const useAuth = () => useContext(AuthContext);

export const AuthProvider = ({ children }) => {
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const storedUser = localStorage.getItem('user');
        if (storedUser) {
            setUser(JSON.parse(storedUser));
        }
        setLoading(false);
    }, []);

    const login = async (email, password) => {
        setLoading(true);
        setError(null);
        try {
            // Mock login by querying users
            const response = await api.get(`/users?email=${email}&password=${password}`);
            const users = response.data;

            if (users.length > 0) {
                const authenticatedUser = users[0];
                // Remove password before storing (optional but good practice)
                // const { password, ...userWithoutPassword } = authenticatedUser; 
                // In this simple mock, we just store what we get
                setUser(authenticatedUser);
                localStorage.setItem('user', JSON.stringify(authenticatedUser));
                toast.success(`Welcome back, ${authenticatedUser.name}!`);
                return true;
            } else {
                setError('Invalid email or password');
                toast.error('Invalid email or password');
                return false;
            }
        } catch (err) {
            setError(err.message || 'Login failed');
            toast.error(err.message || 'Login failed');
            return false;
        } finally {
            setLoading(false);
        }
    };

    const register = async (name, email, password) => {
        setLoading(true);
        setError(null);
        try {
            // Check if user exists
            const checkRes = await api.get(`/users?email=${email}`);
            if (checkRes.data.length > 0) {
                setError('User already exists with this email');
                toast.error('User already exists with this email');
                return false;
            }

            const newUser = {
                id: uuidv4(),
                name,
                email,
                password, // In a real app, never store plain text passwords!
                createdAt: new Date().toISOString()
            };

            const response = await api.post('/users', newUser);

            if (response.status === 201) {
                const createdUser = response.data;
                setUser(createdUser);
                localStorage.setItem('user', JSON.stringify(createdUser));
                toast.success('Account created successfully!');
                return true;
            }
        } catch (err) {
            setError(err.message || 'Registration failed');
            toast.error(err.message || 'Registration failed');
            return false;
        } finally {
            setLoading(false);
        }
    };

    const logout = () => {
        setUser(null);
        localStorage.removeItem('user');
        toast.success('Logged out successfully');
    };

    const updateBudget = async (amount) => {
        if (!user) return false;
        // Don't set global loading here to avoid full page re-renders if not needed, 
        // or keep it if we want to block interaction. Let's keep it simple.
        try {
            const budgetValue = parseFloat(amount);
            await api.patch(`/users/${user.id}`, { budget: budgetValue });

            const updatedUser = { ...user, budget: budgetValue };
            setUser(updatedUser);
            localStorage.setItem('user', JSON.stringify(updatedUser));
            toast.success('Budget updated successfully');
            return true;
        } catch (err) {
            console.error(err);
            toast.error('Failed to update budget');
            return false;
        }
    };

    const value = {
        user,
        loading,
        error,
        login,
        register,
        logout,
        updateBudget
    };

    return (
        <AuthContext.Provider value={value}>
            {!loading && children}
        </AuthContext.Provider>
    );
};

import { createContext, useState, useContext, useEffect, useCallback } from 'react';
import { toast } from 'react-hot-toast';
import api from '../api/axios';
import { useAuth } from './AuthContext';
import { v4 as uuidv4 } from 'uuid';

const ExpenseContext = createContext();

export const useExpenses = () => useContext(ExpenseContext);

export const ExpenseProvider = ({ children }) => {
    const { user } = useAuth();
    const [expenses, setExpenses] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    // Fetch expenses when user logs in
    const fetchExpenses = useCallback(async () => {
        if (!user) {
            setExpenses([]);
            return;
        }

        setLoading(true);
        try {
            const response = await api.get(`/expenses?userId=${user.id}&_sort=date&_order=desc`);
            setExpenses(response.data);
            setError(null);
        } catch (err) {
            setError('Failed to fetch expenses');
            console.error(err);
        } finally {
            setLoading(false);
        }
    }, [user]);

    useEffect(() => {
        fetchExpenses();
    }, [fetchExpenses]);

    const addExpense = async (expenseData) => {
        setLoading(true);
        try {
            const newExpense = {
                id: uuidv4(),
                userId: user.id,
                ...expenseData,
                amount: parseFloat(expenseData.amount), // Ensure number
                createdAt: new Date().toISOString()
            };

            const response = await api.post('/expenses', newExpense);
            setExpenses(prev => [response.data, ...prev]);
            toast.success('Expense added successfully');
            return true;
        } catch (err) {
            setError('Failed to add expense');
            toast.error('Failed to add expense');
            return false;
        } finally {
            setLoading(false);
        }
    };

    const deleteExpense = async (id) => {
        setLoading(true);
        try {
            await api.delete(`/expenses/${id}`);
            setExpenses(prev => prev.filter(exp => exp.id !== id));
            toast.success('Expense deleted successfully');
            return true;
        } catch (err) {
            setError('Failed to delete expense');
            toast.error('Failed to delete expense');
            return false;
        } finally {
            setLoading(false);
        }
    };

    const updateExpense = async (id, updates) => {
        setLoading(true);
        try {
            const updatedData = { ...updates };
            if (updatedData.amount) updatedData.amount = parseFloat(updatedData.amount);

            const response = await api.patch(`/expenses/${id}`, updatedData);
            setExpenses(prev => prev.map(exp => exp.id === id ? response.data : exp));
            toast.success('Expense updated successfully');
            return true;
        } catch (err) {
            setError('Failed to update expense');
            toast.error('Failed to update expense');
            return false;
        } finally {
            setLoading(false);
        }
    };

    const value = {
        expenses,
        loading,
        error,
        addExpense,
        deleteExpense,
        updateExpense,
        refreshExpenses: fetchExpenses
    };

    return (
        <ExpenseContext.Provider value={value}>
            {children}
        </ExpenseContext.Provider>
    );
};

