import { useEffect, useState } from 'react';

/**
 * Custom hook for animating number counting up
 * @param {number} end - The target number to count to
 * @param {number} duration - Animation duration in milliseconds
 * @param {number} delay - Delay before starting animation
 * @returns {number} - The current animated value
 */
export const useCountUp = (end, duration = 2000, delay = 0) => {
    const [count, setCount] = useState(0);

    useEffect(() => {
        if (end === 0) {
            setCount(0);
            return;
        }

        const timeout = setTimeout(() => {
            let startTime;
            let animationFrame;

            const animate = (currentTime) => {
                if (!startTime) startTime = currentTime;
                const progress = Math.min((currentTime - startTime) / duration, 1);

                // Easing function for smooth animation (easeOutQuart)
                const easeOut = 1 - Math.pow(1 - progress, 4);

                setCount(Math.floor(easeOut * end));

                if (progress < 1) {
                    animationFrame = requestAnimationFrame(animate);
                }
            };

            animationFrame = requestAnimationFrame(animate);

            return () => {
                if (animationFrame) {
                    cancelAnimationFrame(animationFrame);
                }
            };
        }, delay);

        return () => clearTimeout(timeout);
    }, [end, duration, delay]);

    return count;
};

import { Container, Row, Col, Badge } from 'react-bootstrap';
import { useExpenses } from '../context/ExpenseContext';
import { calculateInsights } from '../utils/insights';
import { motion } from 'framer-motion';

const Insights = () => {
    const { expenses } = useExpenses();
    const insights = calculateInsights(expenses);

    const getInsightType = (text) => {
        if (text.includes('Alert')) return { color: 'danger', icon: 'bi-exclamation-triangle-fill', bg: 'rgba(239, 68, 68, 0.1)' };
        if (text.includes('Warning')) return { color: 'warning', icon: 'bi-exclamation-circle-fill', bg: 'rgba(245, 158, 11, 0.1)' };
        if (text.includes('Good Job')) return { color: 'success', icon: 'bi-check-circle-fill', bg: 'rgba(16, 185, 129, 0.1)' };
        if (text.includes('Tip')) return { color: 'info', icon: 'bi-lightbulb-fill', bg: 'rgba(6, 182, 212, 0.1)' };
        return { color: 'primary', icon: 'bi-info-circle-fill', bg: 'rgba(99, 102, 241, 0.1)' };
    };

    const containerVariants = {
        hidden: { opacity: 0 },
        visible: {
            opacity: 1,
            transition: { staggerChildren: 0.1 }
        }
    };

    const itemVariants = {
        hidden: { y: 20, opacity: 0 },
        visible: {
            y: 0,
            opacity: 1,
            transition: { type: 'spring', stiffness: 100 }
        }
    };

    return (
        <Container fluid className="px-0 pb-5">
            <motion.div
                initial="hidden"
                animate="visible"
                variants={containerVariants}
            >
                <div className="mb-5 animate-enter">
                    <h1 className="display-4 fw-bold text-gradient mb-2">My Insights</h1>
                    <p className="text-white-50 fs-5">AI-powered financial analysis & smart tips.</p>
                </div>

                <div className="glass-card p-4 p-md-5 mb-5">
                    <div className="d-flex align-items-center mb-5">
                        <div className="bg-gradient-to-r from-indigo-500 to-purple-500 p-3 rounded-circle me-4 text-white shadow-lg d-flex align-items-center justify-content-center" style={{ width: 64, height: 64, background: 'linear-gradient(135deg, #6366f1, #a855f7)' }}>
                            <i className="bi bi-robot fs-2"></i>
                        </div>
                        <div>
                            <h3 className="fw-bold mb-1 text-white">Smart Analysis</h3>
                            <p className="text-white-50 mb-0">Based on your recent spending habits</p>
                        </div>
                    </div>

                    {insights.length > 0 ? (
                        <Row className="g-4">
                            {insights.map((insight, index) => {
                                const type = getInsightType(insight);
                                return (
                                    <Col xs={12} md={6} lg={4} key={index}>
                                        <motion.div
                                            variants={itemVariants}
                                            className="h-100 p-4 rounded-4 position-relative overflow-hidden hover-float"
                                            style={{
                                                background: 'rgba(255,255,255,0.03)',
                                                border: '1px solid rgba(255,255,255,0.05)',
                                                boxShadow: '0 10px 30px -10px rgba(0,0,0,0.5)'
                                            }}
                                        >
                                            <div className="d-flex align-items-start mb-3">
                                                <div className={`p-2 rounded-3 me-3 text-${type.color}`} style={{ background: type.bg }}>
                                                    <i className={`bi ${type.icon} fs-4`}></i>
                                                </div>
                                                <Badge bg={type.color} className="bg-opacity-25 text-white fw-normal px-3 py-2 rounded-pill border border-white border-opacity-10 text-uppercase ls-1" style={{ fontSize: '0.7rem' }}>
                                                    {insight.split(':')[0].replace(/[^a-zA-Z ]/g, '')}
                                                </Badge>
                                            </div>

                                            <p className="text-white lead fs-6 lh-base mb-0"
                                                dangerouslySetInnerHTML={{
                                                    __html: insight.split(':').slice(1).join(':').trim()
                                                        .replace(/\*\*(.*?)\*\*/g, '<strong class="text-white fw-bold">$1</strong>')
                                                }}
                                            />

                                            {/* Decorative blob */}
                                            <div className={`position-absolute rounded-circle bg-${type.color}`} style={{ bottom: -20, right: -20, width: 80, height: 80, opacity: 0.1, filter: 'blur(30px)' }}></div>
                                        </motion.div>
                                    </Col>
                                );
                            })}
                        </Row>
                    ) : (
                        <div className="text-center py-5 text-white-50">
                            <i className="bi bi-clipboard-data display-1 mb-3 d-block opacity-25"></i>
                            <h4 className="fw-normal">No enough data yet</h4>
                            <p>Start adding expenses to unlock personalized insights!</p>
                        </div>
                    )}
                </div>
            </motion.div>
        </Container>
    );
};

