import { useState, useEffect } from 'react';
import { Form, Button, Container, Card, Alert, Spinner, InputGroup, ProgressBar } from 'react-bootstrap';
import { useAuth } from '../context/AuthContext';
import { useNavigate, Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import { HiMail, HiLockClosed, HiEye, HiEyeOff, HiUser, HiCheckCircle } from 'react-icons/hi';

const Register = () => {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [showPassword, setShowPassword] = useState(false);
    const [showConfirmPassword, setShowConfirmPassword] = useState(false);
    const [validationError, setValidationError] = useState('');
    const [passwordStrength, setPasswordStrength] = useState({ score: 0, label: '', color: '' });

    const { register, error } = useAuth();
    const navigate = useNavigate();
    const [isSubmitting, setIsSubmitting] = useState(false);

    // Calculate password strength (Keep existing logic)
    useEffect(() => {
        if (!password) {
            setPasswordStrength({ score: 0, label: '', color: '' });
            return;
        }

        let score = 0;
        let label = '';
        let color = '';

        // Length check
        if (password.length >= 6) score += 25;
        if (password.length >= 8) score += 15;
        if (password.length >= 12) score += 10;

        // Complexity checks
        if (/[a-z]/.test(password)) score += 15;
        if (/[A-Z]/.test(password)) score += 15;
        if (/[0-9]/.test(password)) score += 10;
        if (/[^a-zA-Z0-9]/.test(password)) score += 10;

        // Determine label and color
        if (score < 40) {
            label = 'Weak';
            color = 'danger';
        } else if (score < 70) {
            label = 'Medium';
            color = 'warning';
        } else {
            label = 'Strong';
            color = 'success';
        }

        setPasswordStrength({ score, label, color });
    }, [password]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setValidationError('');

        if (password !== confirmPassword) {
            return setValidationError('Passwords do not match');
        }

        if (password.length < 6) {
            return setValidationError('Password must be at least 6 characters');
        }

        setIsSubmitting(true);
        const success = await register(name, email, password);
        setIsSubmitting(false);
        if (success) {
            navigate('/dashboard');
        }
    };

    return (
        <Container className="d-flex justify-content-center align-items-center min-vh-100 position-relative overflow-hidden py-5">
            {/* Background Decor */}
            <div className="position-absolute top-0 start-0 w-100 h-100 overflow-hidden" style={{ zIndex: -1 }}>
                <div className="position-absolute rounded-circle" style={{ top: '-10%', right: '-10%', width: '60vw', height: '60vw', background: 'radial-gradient(circle, rgba(6,182,212,0.15) 0%, transparent 70%)', filter: 'blur(80px)' }}></div>
                <div className="position-absolute rounded-circle" style={{ bottom: '-10%', left: '-10%', width: '50vw', height: '50vw', background: 'radial-gradient(circle, rgba(239,68,68,0.15) 0%, transparent 70%)', filter: 'blur(80px)' }}></div>
            </div>

            <motion.div
                initial={{ opacity: 0, y: 30 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6, ease: "easeOut" }}
                className="w-100 position-relative z-1"
                style={{ maxWidth: '550px' }}
            >
                <div className="text-center mb-4">
                    <h1 className="fw-bold display-5 text-white mb-1">Join Us</h1>
                    <p className="text-white-50">Create your smart expense manager account</p>
                </div>

                <Card className="glass-card border-0 shadow-2xl overflow-hidden">
                    <div className="position-absolute top-0 start-0 w-100 h-1" style={{ background: 'linear-gradient(90deg, transparent, rgba(255,255,255,0.5), transparent)' }}></div>
                    <Card.Body className="p-4 p-md-5">
                        {error && (
                            <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }}>
                                <Alert variant="danger" className="mb-4 border-0 bg-danger bg-opacity-10 text-danger">{error}</Alert>
                            </motion.div>
                        )}
                        {validationError && (
                            <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }}>
                                <Alert variant="danger" className="mb-4 border-0 bg-danger bg-opacity-10 text-danger">{validationError}</Alert>
                            </motion.div>
                        )}

                        <Form onSubmit={handleSubmit}>
                            <Form.Group className="mb-4">
                                <Form.Label className="text-white-50 small text-uppercase ls-1 fw-bold">Full Name</Form.Label>
                                <InputGroup className="input-group-glass">
                                    <InputGroup.Text className="bg-transparent border-end-0 ps-3 text-white-50"><HiUser className="fs-5" /></InputGroup.Text>
                                    <Form.Control type="text" placeholder="John Doe" required value={name} onChange={(e) => setName(e.target.value)} className="border-start-0 ps-2 py-3 text-white bg-transparent" autoFocus />
                                    {name && <InputGroup.Text className="bg-transparent border-start-0 text-success"><HiCheckCircle /></InputGroup.Text>}
                                </InputGroup>
                            </Form.Group>

                            <Form.Group className="mb-4">
                                <Form.Label className="text-white-50 small text-uppercase ls-1 fw-bold">Email Address</Form.Label>
                                <InputGroup className="input-group-glass">
                                    <InputGroup.Text className="bg-transparent border-end-0 ps-3 text-white-50"><HiMail className="fs-5" /></InputGroup.Text>
                                    <Form.Control type="email" placeholder="name@example.com" required value={email} onChange={(e) => setEmail(e.target.value)} className="border-start-0 ps-2 py-3 text-white bg-transparent" />
                                    {email && email.includes('@') && <InputGroup.Text className="bg-transparent border-start-0 text-success"><HiCheckCircle /></InputGroup.Text>}
                                </InputGroup>
                            </Form.Group>

                            <Form.Group className="mb-4">
                                <Form.Label className="text-white-50 small text-uppercase ls-1 fw-bold">Password</Form.Label>
                                <InputGroup className="input-group-glass">
                                    <InputGroup.Text className="bg-transparent border-end-0 ps-3 text-white-50"><HiLockClosed className="fs-5" /></InputGroup.Text>
                                    <Form.Control type={showPassword ? 'text' : 'password'} placeholder="At least 6 characters" required value={password} onChange={(e) => setPassword(e.target.value)} className="border-start-0 border-end-0 ps-2 py-3 text-white bg-transparent" />
                                    <InputGroup.Text className="bg-transparent border-start-0 pe-3 cursor-pointer text-white-50 hover-text-white" onClick={() => setShowPassword(!showPassword)} style={{ cursor: 'pointer' }}>{showPassword ? <HiEyeOff className="fs-5" /> : <HiEye className="fs-5" />}</InputGroup.Text>
                                </InputGroup>

                                {password && (
                                    <motion.div initial={{ opacity: 0, height: 0 }} animate={{ opacity: 1, height: 'auto' }} className="mt-2">
                                        <div className="d-flex justify-content-between align-items-center mb-1">
                                            <small className="text-white-50" style={{ fontSize: '0.75rem' }}>Strength: <span className={`text-${passwordStrength.color} fw-bold`}>{passwordStrength.label}</span></small>
                                        </div>
                                        <ProgressBar now={passwordStrength.score} variant={passwordStrength.color} style={{ height: '3px', backgroundColor: 'rgba(255,255,255,0.1)' }} />
                                    </motion.div>
                                )}
                            </Form.Group>

                            <Form.Group className="mb-5">
                                <Form.Label className="text-white-50 small text-uppercase ls-1 fw-bold">Confirm Password</Form.Label>
                                <InputGroup className="input-group-glass">
                                    <InputGroup.Text className="bg-transparent border-end-0 ps-3 text-white-50"><HiLockClosed className="fs-5" /></InputGroup.Text>
                                    <Form.Control type={showConfirmPassword ? 'text' : 'password'} placeholder="Confirm password" required value={confirmPassword} onChange={(e) => setConfirmPassword(e.target.value)} className="border-start-0 border-end-0 ps-2 py-3 text-white bg-transparent" />
                                    <InputGroup.Text className="bg-transparent border-start-0 pe-3 cursor-pointer text-white-50 hover-text-white" onClick={() => setShowConfirmPassword(!showConfirmPassword)} style={{ cursor: 'pointer' }}>{showConfirmPassword ? <HiEyeOff className="fs-5" /> : <HiEye className="fs-5" />}</InputGroup.Text>
                                </InputGroup>
                                {confirmPassword && password === confirmPassword && (
                                    <motion.small initial={{ opacity: 0 }} animate={{ opacity: 1 }} className="text-success d-block mt-1 small"><HiCheckCircle className="me-1" /> Match</motion.small>
                                )}
                            </Form.Group>

                            <Button disabled={isSubmitting} className="w-100 py-3 fw-bold rounded-4 btn-primary position-relative overflow-hidden hover-glow" type="submit" size="lg">
                                {isSubmitting ? (
                                    <><Spinner as="span" animation="border" size="sm" role="status" aria-hidden="true" className="me-2" /> Creating...</>
                                ) : (
                                    'Create Account'
                                )}
                            </Button>
                        </Form>

                        <div className="text-center mt-5">
                            <p className="text-white-50 mb-0">
                                Already have an account?{' '}
                                <Link to="/" className="text-white fw-bold text-decoration-none hover-text-primary transition-all position-relative link-hover-effect">
                                    Log In Here
                                </Link>
                            </p>
                        </div>
                    </Card.Body>
                </Card>

                <p className="text-center text-white-50 mt-4 small opacity-50">
                    By signing up, you agree to our Terms & Privacy Policy
                </p>
            </motion.div>
        </Container>
    );
};

export default Register;

import { useParams, useNavigate } from 'react-router-dom';
import { useExpenses } from '../context/ExpenseContext';
import { Card, Button, Container, Row, Col } from 'react-bootstrap';
import { motion } from 'framer-motion';

const TransactionDetails = () => {
    const { id } = useParams();
    const { expenses } = useExpenses();
    const navigate = useNavigate();

    const transaction = expenses.find(item => item.id === id);

    if (!transaction) {
        return (
            <div className="d-flex flex-column align-items-center justify-content-center min-vh-50 text-white-50">
                <i className="bi bi-search fs-1 mb-3 opacity-50"></i>
                <h3>Transaction not found</h3>
                <Button variant="link" className="text-decoration-none text-gradient fw-bold" onClick={() => navigate('/dashboard/expenses')}>
                    <i className="bi bi-arrow-left me-2"></i> Back to Activity
                </Button>
            </div>
        );
    }

    return (
        <Container className="py-4">
            <motion.div
                initial={{ opacity: 0, y: 30 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5 }}
                className="mx-auto"
                style={{ maxWidth: '700px' }}
            >
                <Button variant="link" className="text-decoration-none text-white-50 hover-text-white mb-4 p-0 d-flex align-items-center gap-2" onClick={() => navigate('/dashboard/expenses')}>
                    <div className="rounded-circle bg-white bg-opacity-10 d-flex align-items-center justify-content-center" style={{ width: 32, height: 32 }}>
                        <i className="bi bi-arrow-left"></i>
                    </div>
                    Back to List
                </Button>

                <Card className="glass-card border-0 p-0 overflow-hidden shadow-2xl">
                    <div className="position-absolute top-0 start-0 w-100 h-1" style={{ background: `linear-gradient(90deg, ${transaction.type === 'income' ? '#22c55e' : '#ef4444'}, transparent)` }}></div>

                    <Card.Body className="p-5">
                        <div className="text-center mb-5">
                            <div className={`d-inline-flex p-3 rounded-circle mb-3 ${transaction.type === 'income' ? 'bg-success' : 'bg-danger'} bg-opacity-10 text-${transaction.type === 'income' ? 'success' : 'danger'}`}>
                                <i className={`bi ${transaction.type === 'income' ? 'bi-arrow-down-left' : 'bi-arrow-up-right'} fs-2`}></i>
                            </div>
                            <h6 className="text-uppercase text-white-50 ls-2 small fw-bold mb-2">Transaction Receipt</h6>
                            <h1 className="display-4 fw-bold text-white mb-0">{transaction.title}</h1>
                        </div>

                        <div className="glass-card p-4 rounded-4 mb-4" style={{ background: 'rgba(255,255,255,0.02)' }}>
                            <Row className="gy-4 align-items-center">
                                <Col xs={12} className="text-center border-bottom border-white border-opacity-10 pb-4">
                                    <small className="text-secondary d-block mb-1 text-uppercase ls-1">Total Amount</small>
                                    <h2 className={`display-3 fw-bold mb-0 ${transaction.type === 'income' ? 'text-success' : 'text-danger'}`}>
                                        {transaction.type === 'income' ? '+' : '-'}₹{parseFloat(transaction.amount).toFixed(2)}
                                    </h2>
                                </Col>
                                <Col xs={6}>
                                    <small className="text-secondary d-block mb-1 text-uppercase ls-1" style={{ fontSize: '0.7rem' }}>Category</small>
                                    <span className="fs-5 text-white fw-medium d-flex align-items-center gap-2">
                                        <i className="bi bi-tag-fill opacity-50 text-primary"></i>
                                        {transaction.category}
                                    </span>
                                </Col>
                                <Col xs={6} className="text-end">
                                    <small className="text-secondary d-block mb-1 text-uppercase ls-1" style={{ fontSize: '0.7rem' }}>Date</small>
                                    <span className="fs-5 text-white fw-medium">
                                        {new Date(transaction.date).toLocaleDateString(undefined, { month: 'short', day: 'numeric', year: 'numeric' })}
                                    </span>
                                </Col>
                            </Row>
                        </div>

                        <div className="d-flex justify-content-between align-items-center text-white-50 small font-monospace">
                            <span>ID: {transaction.id.substring(0, 8)}...</span>
                            <span>{new Date(transaction.createdAt || Date.now()).toLocaleTimeString()}</span>
                        </div>
                    </Card.Body>
                </Card>
            </motion.div>
        </Container>
    );
};

export default TransactionDetails;
